mapper.select:
SELECT P.project_name,T.task_report_id,T.project_id,T.user_id,T.jira_project_id,T.jira_task_id,T.start_date,T.task_name,T.hours_spent,T.created_at FROM task_report T INNER JOIN project P ON P.project_id = T.project_id
WHERE T.user_id = #{userId}
ORDER BY T.created_at DESC

mapper.update:
UPDATE task_report
SET
jira_task_id = #{jiraTaskId},
jira_project_id = #{jiraProjectId}
WHERE
task_report_id = #{taskReportId}

UPDATE task_report
SET
is_paid = true,
is_archived = true
WHERE task_report_id IN (
        SELECT task_report_id FROM task_report  WHERE extract(month from created_at) = #{month}
        AND extract(year from created_at) = #{year}
    )
AND user_id = #{userId}
